mapper.update:
update ums_wallet set  frozen_balance =   frozen_balance + #{amount},
                       balance = balance - #{amount}
where coin = #{coin}
and
      member_id = #{memberId}
and
      balance >= #{amount}

update ums_wallet set  frozen_balance =   frozen_balance - #{amount},
                       balance = balance + #{amount}
where coin = #{coin}
  and
    member_id = #{memberId}
  and
    frozen_balance >= #{amount}

update ums_wallet set  frozen_balance =   frozen_balance - #{amount}
where coin = #{coin}
  and
    member_id = #{memberId}
  and
    frozen_balance >= #{amount}
